import { DISPLAY_PROD, REMOVE_ALL } from "../Actions/productActions";

//innitial object to avoid errors of undefined state in componenents
const initialUser = {
  id: "",
};

export function productReducer(state = initialUser, action) {
  switch (action.type) {
    case DISPLAY_PROD:
      // overwriting the new user object to the existing array
      return {
        id: action.payload,
      };
    case REMOVE_ALL:
      //removing all but the innitial state object
      return {
        id: "",
      };
    default:
      return state;
  }
}
