import { Dashboard } from "./Dashboard";
import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
//gets the product id from the called page

export function Detail() {
  function Back() {
    window.history.back();
  }
  const location = useLocation();
  const id = location.state?.id;

  //gets the product details from the product id
  const [allproducts, setAll] = useState([]);
  const [product, setProduct] = useState({});

  useEffect(() => {
    //getting all 6 products and search for the id
    axios
      .get("https://fakestoreapi.com/products/category/electronics", {
        params: { limit: 6 },
      })
      .then((res) => {
        const foundProduct = res.data.find((obj) => obj.id === id);
        setProduct(foundProduct || {});
      })
      .catch((err) => {
        console.log("Error getting data");
      });
    //getting the product details from the id
    //search in the allproducts array for the id
    //set the product state to the product details
    // for (const obj of allproducts) {
    //   if (obj.id === id) {
    //     setProduct(obj);
    //   }
    // }
  }, [id]);

  return (
    <Dashboard>
      <div>
        <h1>Details "{product.id} "</h1>
        <div className="details-container">
          <div className="details-img">
            <img src={product.image} alt="product" />
          </div>
          <div className="details-info">
            <ul>
              <li>
                <h4>{product.name}</h4>
              </li>
              <li>
                {product.rating} Stars ({product.numReviews} Reviews)
              </li>
              <li>
                Price: <b>${product.price}</b>
              </li>
              <li>
                Description:
                <div>{product.description}</div>
              </li>
            </ul>
          </div>
          <div className="details-action">
            <ul>
              {/* <li>Price: {product.price}</li>
              <li>Status: {product.status}</li> */}
              <li>
                <button className="Button">Remove From Cart</button>
                <button className="Button">Add to Cart</button>
                <button className="Button" onClick={Back}>
                  Back
                </button>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </Dashboard>
  );
}
