import { Dashboard } from "./Dashboard";
import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { E_URL } from "../url";
//gets the product id from the called page

export function Detail() {
  function Back() {
    window.history.back();
  }
  const location = useLocation();
  const id = location.state?.id;

  //gets the product details from the product id
  const [product, setProduct] = useState({});

  const dispatch = useDispatch();

  function Add() {
    console.log("ADDING");
    dispatch({ type: "ADD", payload: product });
    alert("Added to Cart");
  }

  function Remove(id) {
    console.log("REMOVING");
    dispatch({ type: "REMOVE", payload: id });
    alert("Removed from Cart");
  }

  useEffect(() => {
    //getting all 6 products and search for the id
    axios
      .get(E_URL, {
        params: { limit: 6 },
      })
      .then((res) => {
        const foundProduct = res.data.find((obj) => obj.id === id);
        setProduct(foundProduct || {});
      })
      .catch((err) => {
        console.log("Error getting data");
      });
  }, [id]);

  return (
    <Dashboard>
      <div>
        <h1>Details "{product.id}"</h1>
        <div className="details-container">
          <div className="details-img">
            <img src={product.image} alt="product" />
          </div>
          <div className="details-info">
            <ul>
              <li>
                <h4>{product.name}</h4>
              </li>
              <li>
                {product.rating && `${product.rating} Stars`} (
                {product.numReviews && `${product.numReviews} Reviews`})
              </li>
              <li>{product.price && `Price: $${product.price}`}</li>
              <li>
                Description:
                <div>{product.description}</div>
              </li>
            </ul>
          </div>
          <div className="details-action">
            <ul>
              <li>
                <button className="Button" onClick={Remove(product.id)}>
                  Remove From Cart
                </button>
                <button
                  className="Button"
                  onClick={Add(
                    product.id,
                    product.name,
                    product.price,
                    product.img
                  )}
                >
                  Add to Cart
                </button>
                <button className="Button" onClick={Back}>
                  Back
                </button>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </Dashboard>
  );
}
