const LOGIN_USER = "LOGIN_USER";
const LOGOUT_USER = "LOGOUT_USER";

const initialUser = {
  users: [
    {
      id: 0,
      email: "",
      password: "",
      isLoggedIn: false,
    },
  ],
};

export function userReducer(state = initialUser, action) {
  switch (action.type) {
    case LOGIN_USER:
      // Appending the new user object to the existing array
      return {
        ...state,
        users: [...state.users, action.payload], // Assuming action.payload contains the new user object
      };

    case LOGOUT_USER:
      const updatedUsers = state.users.slice(0, -1);

      return {
        ...state,
        users: updatedUsers,
      };
    default:
      return state;
  }
}
