export const DISPLAY_PROD = "DISPLAY_PROD";
export const REMOVE_ALL = "REMOVE_ALL";

//returning an action object
export const displayProd = (id) => ({
  type: DISPLAY_PROD,
  payload: {
    id,
  },
});

export const removeall = () => ({
  type: REMOVE_ALL,
});
