import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux"; // Import useSelector and useDispatch for functional components
import { loginUser } from "../redux/Actions/userActions"; // Import action types
import { useNavigate } from "react-router-dom";
import { registerUser } from "../redux/Actions/registerActions";
import { U_URL } from "../url";

export function Login() {
  const [enteredEmail, setEmail] = useState("");
  const [enteredPassword, setPassword] = useState("");
  const [isAdmin, setAdmin] = useState(false);

  const [userData, setData] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);
  const [userID, setID] = useState(0);

  const dispatch = useDispatch();
  const userLogged = useSelector((state) => state.user);
  const registered = useSelector((state) => state.userReg);
  const navigate = useNavigate();

  useEffect(() => {
    // Axios API call
    axios
      .get(U_URL, { params: { limit: 5 } })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
    for (const obj of userData) {
      dispatch(
        registerUser(
          obj.id,
          obj.name.firstname,
          obj.name.obj.email,
          obj.password
        )
      );
    }
  }, []);

  useEffect(() => {
    if (loggedIn) {
      dispatch(loginUser(userID, enteredEmail, enteredPassword, isAdmin));
      console.log(userLogged);

      navigate("/user");
    }
  }, [loggedIn]);

  function authenticate() {
    console.log(enteredEmail, enteredPassword, isAdmin);
    if (isAdmin === true) {
      if (enteredEmail === "admin" && enteredPassword === "admin") {
        setLoggedIn(true);
      } else {
        alert("Invalid Credentials");
        setLoggedIn(false);
      }
    } else {
      for (const obj of userData) {
        if (obj.email === enteredEmail && obj.password === enteredPassword) {
          console.log(obj.email, enteredEmail, obj.password, enteredPassword);
          setLoggedIn(true);
          setID(obj.id);
          break;
        } else {
          alert("Invalid Credentials");
          setLoggedIn(false);
        }
      }
    }
  }

  return (
    <div className="Login">
      <div className="Container">
        <form onSubmit={authenticate}>
          <h2>Enter Login Details</h2>
          <br />
          <input
            className="Input"
            type="text"
            id="email"
            placeholder="email"
            value={enteredEmail}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            className="Input"
            type="password"
            id="password"
            placeholder="password"
            value={enteredPassword}
            onChange={(e) => setPassword(e.target.value)}
          />
          <br />
        </form>
        <form>
          <input
            type="checkbox"
            className="Checkbox"
            check={isAdmin}
            onChange={(e) => setAdmin(e.target.checked)}
          />
          <label className="Checkbox-label">Admin</label>
        </form>
        <br />

        <button className="Button" onClick={authenticate}>
          Login
        </button>
        <button className="Button" onClick={() => navigate("/")}>
          Home
        </button>
        <button className="Button" onClick={() => navigate("/register")}>
          Register
        </button>
      </div>
    </div>
  );
}
