import "./globalCSS.css";
import { Home } from "./pages/home";
import { Route, Routes } from "react-router-dom";
import { Login } from "./pages/login";
import { User } from "./pages/user";
import { Cart } from "./components/cart";
import { Protected } from "./components/authenticated";
import { useEffect, useState } from "react";

import { useSelector } from "react-redux";

function App() {
  const userLogged = useSelector((state) => state.user);
  const loggedInUser = useState();
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Local state to track login status

  useEffect(() => {
    // Check if the user is logged in
    loggedInUser = userLogged.find((user) => user.isLoggedIn);
    setIsLoggedIn(loggedInUser !== undefined);
  }, [userLogged]);

  return (
    <div className="App-body">
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route element={<Protected userID={loggedInUser.id} />}>
          <Route path="/user" element={<User />} />
          <Route path="/cart" element={<Cart />} />
        </Route>
      </Routes>
    </div>
  );
}

export default App;
