import { Dashboard } from "../layout/Dashboard";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux"; // Import useSelector and useDispatch for functional components
import { loginUser } from "../redux/Actions/userActions"; // Import action types

export function Login() {
  const [enteredEmail, setEmail] = useState("");
  const [enteredPassword, setPassword] = useState("");

  const [userData, setData] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  const dispatch = useDispatch();

  useEffect(() => {
    // Axios API call
    axios
      .get("https://fakestoreapi.com/users", { params: { limit: 5 } })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  function authenticate() {
    for (const obj of userData) {
      if (obj.email === enteredEmail && obj.password === enteredPassword) {
        console.log(obj.email, enteredEmail, obj.password, enteredPassword);
        setLoggedIn(true);
        break;
      }
    }

    console.log(loggedIn);
    if (loggedIn) {
      dispatch(loginUser(enteredEmail, enteredPassword));
      alert("Login Successful");
    }
  }

  return (
    <Dashboard>
      <div className="Login">
        <div className="Login-Container">
          <h2>Enter Login Details</h2>
          <br />
          <input
            className="Input"
            type="text"
            id="email"
            placeholder="email"
            value={enteredEmail}
            onChange={(e) => setEmail(e.target.value)}
          />

          <input
            className="Input"
            type="password"
            id="password"
            placeholder="password"
            value={enteredPassword}
            onChange={(e) => setPassword(e.target.value)}
          />
          <br />
          <button className=" Button" onClick={authenticate}>
            Login
          </button>
        </div>
      </div>
    </Dashboard>
  );
}
