import { Dashboard } from "./Dashboard";
import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { E_URL } from "../url";
import { addToCart, removeFromCart } from "../redux/Actions/cartActions";
import "./detailStyle.css";

//gets the product id from the called page

export function Detail() {
  function Back() {
    window.history.back();
  }
  const location = useLocation();
  const id = location.state?.id;

  //gets the product details from the product id
  const [product, setProduct] = useState({});

  const dispatch = useDispatch();

  function RemoveCart() {
    console.log("remove cart");
    dispatch(removeFromCart(product.id));
    alert("Removed from cart");
  }

  function AddCart(id, name, price, image) {
    console.log("add cart");
    dispatch(addToCart(id, name, price, image));
    alert("Added to cart");
  }
  useEffect(() => {
    //getting all 6 products and search for the id
    axios
      .get(E_URL, {
        params: { limit: 6 },
      })
      .then((res) => {
        const foundProduct = res.data.find((obj) => obj.id === id);
        setProduct(foundProduct || {});
      })
      .catch((err) => {
        console.log("Error getting data");
      });
  }, [id]);

  return (
    <Dashboard>
      <div>
        <h1>Details "{product.id}"</h1>
        <div className="details-container">
          <div className="details-img">
            <img src={product.image} alt="product" />
          </div>
          <div className="details-info">
            <ul>
              <li>
                <h4>{product.title}</h4>
              </li>
              <li>{product.category && `Category: ${product.category}`}</li>
              <li>{product.price && `Price: $${product.price}`}</li>
              <li>
                Description:
                <div>{product.description}</div>
              </li>
            </ul>
          </div>
        </div>
        <div className="details-action">
          <ul>
            <li>
              <button className="Button" onClick={Back}>
                Back
              </button>
            </li>
            <li>
              <button className="Button" onClick={() => RemoveCart(product.id)}>
                Remove From Cart
              </button>
            </li>
            <li>
              <button
                className="Button"
                onClick={() =>
                  AddCart(
                    product.id,
                    product.title,
                    product.price,
                    product.image
                  )
                }
              >
                Add to Cart
              </button>
            </li>
          </ul>
        </div>
      </div>
    </Dashboard>
  );
}
